import React, { Component } from 'react'
import axios from 'axios'


class Writeup extends Component {

    constructor(props) {
        super(props)
        this.state = {
            analysis: {},
            value: ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
    }

    handleSubmit(event) {
        alert('Your text was analyzed ' + this.state.value);
        event.preventDefault();

        axios.post("http://127.0.0.1:5000/writeup", this.state, {headers:{
            "Access-Control-Allow-Methods": "POST, PUT",
            "Access-Control-Allow-Origin": "http://127.0.0.1:5000/writeup",
        }}).then(response => {
            this.setState({
                analysis: response.data
            })
            console.log(response.data)
            console.log(response.headers)
            console.log(response.data.ID)
        })
    }

    // componentDidMount() {
    //     axios.get("http://127.0.0.1:5000/writeup").then(response => {
    //         this.setState({
    //             analysis: response.data
    //         })
    //         console.log(response.data)
    //         console.log(response.headers)
    //         console.log(response.data.ID)


    //     })
    // }


    render() {
        const { analysis } = this.state
        return (
            <div><form onSubmit={this.handleSubmit}>
                <label>
                    Enter your text here:
                    <input type="text" value={this.state.value} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
            </form><h1>Your writing tone"</h1><div>
                    {analysis.ID}{analysis.Message}{analysis.Tone}</div></div>
        )
    }

}


export default Writeup;
